query accounts {
  onboardings {
    edges {
      node {
        accountHolder {
          statusInfo {
            status
            ... on AccountHolderCanceledStatusInfo {
              __typename
              reason
              status
            }
            ... on AccountHolderEnabledStatusInfo {
              __typename
              status
            }
            ... on AccountHolderSuspendedStatusInfo {
              __typename
              reason
              status
            }
          }
          verificationStatus
