query accounts {
  accountHolders {
    edges {
      node {
        accounts {
          edges {
            node {
              number
              IBAN
              BIC
              id
              virtualIbanEntries {
                edges {
                  node {
                    id
                    IBAN
                    BIC
                  }
                }
              }
