                  currency
                  value
                }
              }
            }
          }
        }
      }
    }
  }
}

mutation MyMutation($accountId: ID!) {
  addVirtualIbanEntry(input: {accountId: $accountId}) {
    ... on AddVirtualIbanEntrySuccessPayload {
      __typename
      virtualIbanEntry {
        IBAN
        status
      }
    }
    ... on AccountNotFoundRejection {
      id
      message
    }
  }
}

query MyQuery {
  accounts {
    edges {
      node {
        name
        number
        IBAN
        transactions {
          edges {
            node {
              amount {
                currency
                value
              }
              bookedBalanceAfter {
                currency
                value
              }
              counterparty
              createdAt
              id
              label
              payment {
                createdAt
                id
                statusInfo {
                  status
                  ... on PaymentConsentPending {
                    __typename
                    consent {
                      createdAt
                      startedAt
                      status
                      updatedAt
                    }
                    status
                  }
                  ... on PaymentInitiated {
                    __typename
                    status
                  }
                  ... on PaymentRejected {
                    __typename
                    reason
                    status
                  }
                }
                updatedAt
              }
              paymentId
              paymentMethodIdentifier
              paymentProduct
              reference
              side
              statusInfo {
                status
                ... on BookedTransactionStatusInfo {
                  __typename
                  bookingDate
                  status
                  valueDate
                }
                ... on CanceledTransactionStatusInfo {
                  canceledDate
                  status
                }
                ... on PendingTransactionStatusInfo {
                  pendingEndDate
                  status
                }
                ... on RejectedTransactionStatusInfo {
                  __typename
                  reason
                  status
                }
                ... on UpcomingTransactionStatusInfo {
                  __typename
                  executionDate
                  status
                }
              }
              type
              updatedAt
              ... on SEPACreditTransferTransaction {
                id
                amount {
                  currency
                  value
                }
                bookedBalanceAfter {
                  currency
                  value
                }
                counterparty
                createdAt
                creditor {
                  name
                  ... on SEPACreditTransferInCreditor {
                    virtualIBANEntryId
                    accountNumber
                    maskedIBAN
                    name
                  }
                  ... on SEPACreditTransferInternalOutCreditor {
                    __typename
                    accountNumber
                    beneficiary {
                      accountNumber
                      id
                      isMyOwnIban
                      name
                    }
                    name
                  }
                  ... on SEPACreditTransferOutCreditor {
                    __typename
                    beneficiary {
                      isMyOwnIban
                      maskedIBAN
                      name
                    }
                    maskedIBAN
                    name
                  }
                }
                debtor {
                  name
                  ... on SEPACreditTransferInDebtor {
                    __typename
                    maskedIBAN
                    name
                  }
                  ... on SEPACreditTransferInternalInDebtor {
                    __typename
                    accountNumber
                    name
                  }
                  ... on SEPACreditTransferOutDebtor {
                    __typename
                    maskedIBAN
                    name
                    accountNumber
                  }
                }
                label
                paymentId
                paymentMethodIdentifier
                paymentProduct
                reference
                side
                statusInfo {
                  status
                }
                type
                updatedAt
              }
            }
          }
        }

      }
    }
  }
}

